INSERT INTO `conferences` (`id`, `title`, `image`, `details`, `created_at`, `updated_at`) VALUES
(15, 'المؤتمر القرآني الدولي الثاني في هدايات القرآن الكريم', '1560014923.jpg', '<p style=\"text-align: center;\">&nbsp;</p>\r\n<h4 style=\"text-align: center;\">تعظيم الله تعالى في هدايات القرآن الكريم</h4>\r\n<p style=\"text-align: center;\"><br />تنظيم جامعة أفريقيا العالمية بالشراكة مع كرسي الهدايات القرآنية بجامعة أم القرى</p>\r\n<h5 style=\"text-align: center;\">في الفترة من 14 - 1441/5/16هـ الموافق 9 ـ 11 يناير 2020م</h5>\r\n<div class=\"center text-center\" style=\"text-align: center;\"><img class=\"img-responsive \" title=\"المؤتمر القرآني الدولي الثاني\" src=\"../../../../template/img/conferencePoster.jpg\" alt=\"المؤتمر القرآني الدولي الثاني في هدايات القرآن الكريم\" width=\"807\" height=\"1280\" />&nbsp;</div>\r\n<ul>\r\n<li style=\"display: block; background-color: #87744e; font-size: small; color: white;\">\r\n<h4 style=\"color: white; text-align: center;\"><a class=\"btn\" href=\"../../../../uploads/BruchureConferenceSudan.pdf\" target=\"_blank\">تنزيل البرشور الخاص بالمؤتمر </a></h4>\r\n</li>\r\n</ul>\r\n<p style=\"text-align: center;\">&nbsp;</p>', '2019-06-09 15:14:59', '2019-06-08 09:28:43'),
(6, 'مؤتمر الهدايات القرآنية الأول بماليزيا', '1551672127.jpg', '<p style=\"text-align: center;\">&nbsp;</p>\r\n<h4 style=\"text-align: center;\">مؤتمر الهدايات القرآنية الأول بماليزيا</h4>\r\n<p style=\"text-align: center;\"><br />تنظيم مركز بحوث القرآن الكريم بجامعة ملايا - كوالالمبور - ماليزيا</p>\r\n<h5 style=\"text-align: center;\">في الفترة من 6 - 1440/3/7 الموافق 14 ـ 15 نوفمبر 2018م</h5>\r\n<div class=\"center text-center\" style=\"text-align: center;\"><img class=\"img-responsive \" title=\"المؤتمر القرآني الدولي الثاني\" src=\"../../../../template/img/malaysia-conference.jpeg\" alt=\"المؤتمر القرآني الدولي الثاني في هدايات القرآن الكريم\" width=\"807\" height=\"1280\" />&nbsp;</div>\r\n<ul>\r\n<li style=\"display: block; background-color: #87744e; font-size: small; color: white;\">\r\n<h4 style=\"color: white; text-align: center;\"><a class=\"btn\" href=\"../../../../uploads/BruchureConferenceMalaysia.pdf\" target=\"_blank\">تنزيل البرشور الخاص بالمؤتمر </a></h4>\r\n</li>\r\n</ul>\r\n<p style=\"text-align: center;\">&nbsp;</p>', '2019-06-20 10:39:00', '2018-10-25 15:44:23'),
(12, ':: تدشين البوابة الالكترونية العالمية ::', '1551671064.JPG', '<p style=\"text-align: center;\"><span style=\"font-size: x-large;\"><strong>إطلاق البوابة الالكترونية للموسوعة</strong></span></p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<p style=\"text-align: right;\"><span style=\"font-size: large;\"><strong>قامت إدارة الموسوعة العالمية للهدايات القرآنية بتدشين البوابة الالكترونية للموسوعة والتي تساعد في عملية إدارة وتحكيم الأبحاث بشكل أكثر سلاسة. حيث تتيح البوابة للباحثين والمشرفين وأعضاء اللجنة العلمية متابعة بحوثات الطلاب والتقارير المرسلة من قبلهم ومن قبل مشرفيهم بشكل مباشر. كما يستطيع الطالب أيضا الحصول على تقرير ومرئيات اللجنة العلمية عن ابحاثه بشكل فوري.&nbsp;</strong></span></p>\r\n<p style=\"text-align: right;\" dir=\"rtl\"><span style=\"font-size: large;\"><strong>بالإضافة إلى ذلك، تتيح البوابة للطلاب التواصل مع المشرفين والإدارة بشكل أسهل وأفضل. حيث تسعى إدارة الموسوعة إلى تطوير فنية وتقنية متقدمة من شأنها تسهيل إعداد الدراسات البحثية وحتى الانتهاء من مشروع الهدايات القرآنية.</strong></span></p>', '2019-06-09 15:02:16', '2019-03-04 03:23:32');
